export async function setLocale(locale?: string): Promise<void> {
  let localeToSet = locale
  if (locale === undefined || locale === null) {
    localeToSet = navigator.language
  }
  if (localeToSet === undefined) {
    return
  }
  await fetchLocale(localeToSet)
  i18n.global.locale.value = localeToSet
}

export async function setFallbackLocale(locale: string): Promise<void> {
  await fetchLocale(locale)
  i18n.global.fallbackLocale.value = locale
}

