const baseURL = process.env.VUE_APP_LOCALE_URL
const localeVersion = process.env.VUE_APP_LOCALE_VERSION

async function fetchLocale(locale: string): Promise<void> {
  // locales can be in the format of 2 character language-country
  const localeParts = locale.split('-')
  const lang = localeParts[0]
  let langJSON = undefined
  // first try to get the base language (most likely to pass)
  if (!localeLoaded(lang)) {
    langJSON = await fetchLocaleJson(lang)
  }
  // next try to get a  more specific country language
  let countryJSON = undefined
  if (localeParts.length > 1 && !localeLoaded(locale)) {
    countryJSON = await fetchLocaleJson(locale)
  }
  // if the more specific language is there set it first to prevent two back-to-back re-renders
  if (countryJSON !== undefined) {
    i18n.global.setLocaleMessage(locale, countryJSON)
  }
  if (langJSON !== undefined) {
    i18n.global.setLocaleMessage(lang, langJSON)
  }
}

function localeLoaded(locale: string): boolean {
  return i18n.global.availableLocales.includes(locale)
}

async function fetchLocaleJson(locale: string): Promise<any> {
  const url = `${baseURL}/${localeVersion}/${locale.toLowerCase()}.json`
  return await fetch(url)
    .then((res) => res.json())
    .catch(() => undefined)
}
