interface IUseAddDevice {
  addDevice: () => Promise<PassageDevice | undefined>
  addPending: Ref<boolean>
  errorMessage: Ref<string>
}

export default function useAddDevice(appId: string): IUseAddDevice {
  const addPending = ref<boolean>(false)
  const errorMessage = ref<string>('')

  async function addDevice(): Promise<PassageDevice | undefined> {
    errorMessage.value = ''
    addPending.value = true
    try {
      const passageUser = new Passage(appId).getCurrentUser()
      return await passageUser.addDevice()
    } catch {
      errorMessage.value = useLocale().t('failed-to-add-this-device')
      return undefined
    } finally {
      addPending.value = false
    }
  }

  return {
    addDevice,
    addPending,
    errorMessage,
  }
}
