import { Passage, PassageAppInfo } from '@passageidentity/passage-js'
import { onMounted, Ref, ref, watch } from 'vue'
import { useAppId } from './useAppId'
import { setFallbackLocale, useLocale } from './useLocale'

export interface IUseAppInfo {
  appInfo: Ref<PassageAppInfo | undefined>
  webauthnAllowed: Ref<boolean>
  invalidAppId: Ref<boolean>
  loading: Ref<boolean>
}

export function useAppInfo(appId?: string): IUseAppInfo {
  const activeAppId = appId === undefined ? useAppId().appId : ref<string>(appId)
  const roamingAuthenticatorsAllowed = false
  const webauthnAllowed = ref<boolean>(false)
  const invalidAppId = ref<boolean>(false)
  const loading = ref<boolean>(true)
  const appInfo = ref<PassageAppInfo>()

  async function execute(): Promise<void> {
    invalidAppId.value = false
    const passage = new Passage(activeAppId.value)
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    try {
      // check that the config is correct
      appInfo.value = await passage.appInfo()
      await setFallbackLocale(appInfo.value.default_language)
      if (passage.checkWebauthnConfig(appInfo.value)) {
        webauthnAllowed.value = await passage.isWebauthnSupported(roamingAuthenticatorsAllowed)
      } else {
        // config is bad (auth_origin/fqdn)
        webauthnAllowed.value = false
      }
      invalidAppId.value = false
      loading.value = false
    } catch (err) {
      invalidAppId.value = true
      loading.value = false
    }
  }

  onMounted(async () => {
    await execute()
  })

  watch(activeAppId, () => {
    execute()
  })

  return {
    appInfo,
    webauthnAllowed,
    invalidAppId,
    loading,
  }
}
