      const isValidPhone = !props.modelValue || (phoneInput?.isValidNumber() ?? false)
      if (!isValidPhone) {
        errorMessage.value = t('invalid-phone')
      } else {
        const number = phoneInput?.getNumber()
        if (number) {
          context.emit('update:modelValue', phoneInput?.getNumber())
        }
      }
      return isValidPhone
    }

    const phoneInputBox = ref()
    const controlContainer = ref()

    const { defaultCountryCode } = useDefaultCountryCode()
    let phoneInput: intlTelInput.Plugin | undefined
    function showCountryCode() {
      phoneInput = intlTelInput(phoneInputBox.value, {
        initialCountry: phoneInput?.getSelectedCountryData().iso2 ?? defaultCountryCode.value,
        utilsScript: 'https://cdnjs.cloudflare.com/ajax/libs/intl-tel-input/17.0.8/js/utils.js',
        dropdownContainer: controlContainer.value,
        autoPlaceholder: 'aggressive',
      })
    }

    function hideCountryCode() {
      if (phoneInput === undefined) {
        return
      }
      phoneInput.destroy()
      phoneInput = undefined
    }

    onMounted(() => {
      if (!props.readonly) {
        showCountryCode()
      }
    })

    watch(
      () => props.readonly,
      () => {
        if (!props.readonly) {
          showCountryCode()
        } else {
          hideCountryCode()
        }
      },
    )

    return {
      reactiveValue,
      validate,
      errorMessage,
      phoneInputBox,
      controlContainer,
    }
  },
})
</script>
<!-- todo: disable eslint for no-mutating-props - requires a refactor later -->
<!-- eslint-disable vue/no-mutating-props -->
<template>
  <div
    class="grid-stack"
    :class="{ profile: layoutType === LayoutType.Profile, register: layoutType === LayoutType.Registration }"
    ref="gridContainer"
  >
    <div
      :id="item.id"
      class="grid-stack-item"
      v-for="item in items"
      :key="item.id"
      :gs-id="item.id"
      :gs-x="item.x"
      :gs-y="item.y"
      :gs-w="item.w"
      :gs-h="item.h"
    >
      <div class="grid-stack-item-content">
        <component
          :is="item.component"
          :label="item.friendly_name"
          :placeholder="item.placeholder"
          :required="layoutType === LayoutType.Registration"
          v-model="userMetadata[item.field_name]"
          :ref="setRef"
          :readonly="!isEditing"
        />
      </div>
    </div>
  </div>
</template>
<!-- eslint-enable vue/no-mutating-props -->

<script lang="ts">
import {
  Metadata,
  PassageAppInfo,
  UserMetadataLayout,
  UserMetadataSchema,
  UserMetadataType,
} from '@passageidentity/passage-js'
import { defineComponent, PropType, computed, ref, onMounted, ComponentInternalInstance } from 'vue'
import { GridStack } from 'gridstack'
import Checkbox from '@/ui-components/Checkbox.vue'
import ValidatedString from '../ValidatedInputs/ValidatedString.vue'
import ValidatedDate from '../ValidatedInputs/ValidatedDate.vue'
import ValidatedBoolean from '../ValidatedInputs/ValidatedBoolean.vue'
import ValidatedEmail from '../ValidatedInputs/ValidatedEmail.vue'
import ValidatedInteger from '../ValidatedInputs/ValidatedInteger.vue'
import ValidatedPhone from '../ValidatedInputs/ValidatedPhone.vue'
import { useLocale } from '@/composables/useLocale'

export enum LayoutType {
  Registration = 'registration',
  Profile = 'profile',
}

export default defineComponent({
  name: 'User Metadata',
  components: {
    Checkbox,
    ValidatedString,
    ValidatedDate,
    ValidatedBoolean,
    ValidatedEmail,
    ValidatedInteger,
    ValidatedPhone,
  },
  props: {
    appInfo: {
      type: Object as PropType<PassageAppInfo>,
      required: true,
    },
    layoutType: {
      type: String as () => LayoutType,
      required: true,
    },
    userMetadata: {
      type: Object as PropType<Metadata>,
      required: true,
    },
    isEditing: {
      type: Boolean,
      default: true,
    },
  },
  setup(props) {
    const { t } = useLocale()
    const layout = computed<UserMetadataLayout[]>(() => {
      return props.layoutType === LayoutType.Registration
        ? props.appInfo.layouts.registration
        : props.appInfo.layouts.profile
    })

    const schema = computed<UserMetadataSchema[]>(() => {
      return props.appInfo.user_metadata_schema
    })

    interface ILayoutItem extends UserMetadataLayout, UserMetadataSchema {
      placeholder: string
      component: string
    }

    const items = computed<ILayoutItem[]>(() => {
      return layout.value.map((layout) => {
        const foundSchema = schema.value.find((schema) => schema.id === layout.id)
        return {
          ...layout,
          // eslint-disable-next-line @typescript-eslint/no-non-null-assertion
          ...foundSchema!,
          placeholder: getPlaceholder(foundSchema),
          component: getComponent(foundSchema),
        }
      })
    })

    function getPlaceholder(schema: UserMetadataSchema | undefined): string {
      if (schema === undefined) {
        return ''
      }
      switch (schema.type) {
        case UserMetadataType.STRING:
          return t('enter-schema-friendly_name', [schema.friendly_name.toLowerCase()])
        case UserMetadataType.BOOLEAN:
          return ''
        case UserMetadataType.INTEGER:
          return '0'
        case UserMetadataType.DATE:
          return 'MM/DD/YYYY'
        case UserMetadataType.PHONE:
          return '(555) 555-5555'
        case UserMetadataType.EMAIL:
          return 'example@email.com'
      }
    }

    function getComponent(schema: UserMetadataSchema | undefined) {
      if (schema === undefined) {
        return 'ValidatedString'
      }
      switch (schema.type) {
        case UserMetadataType.STRING:
          return 'ValidatedString'
        case UserMetadataType.BOOLEAN:
          return 'ValidatedBoolean'
        case UserMetadataType.INTEGER:
          return 'ValidatedInteger'
        case UserMetadataType.DATE:
          return 'ValidatedDate'
        case UserMetadataType.PHONE:
          return 'ValidatedPhone'
        case UserMetadataType.EMAIL:
          return 'ValidatedEmail'
      }
    }

    const resizeObserver = new ResizeObserver(() => {
      grid.onParentResize()
    })

    let grid: GridStack
    const gridContainer = ref<HTMLElement>()
    onMounted(() => {
      if (!gridContainer.value) {
        return
      }
      grid = GridStack.init(
        {
          acceptWidgets: true,
          minRow: 1,
          cellHeight: '75px',
          minWidth: 400,
          column: 6,
          margin: '0px 10px',
          resizable: {
            handles: 'e,w',
          },
          staticGrid: true,
        },
        gridContainer.value,
      )
      resizeObserver.observe(gridContainer.value)
    })

    const inputComponents: ComponentInternalInstance[] = []
    function setRef(component: ComponentInternalInstance) {
      if (component) {
        inputComponents.push(component)
      }
    }

    function validate(): boolean {
      let allValid = true
      inputComponents.forEach((component) => {
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        const isValid = (component as any).validate()
        if (!isValid) {
          allValid = false
        }
      })
      return allValid
    }

    return {
      layout,
      schema,
      items,
      gridContainer,
      setRef,
      validate,
      LayoutType,
    }
  },
})
</script>
import { useLocale } from '@/composables/useLocale'
import { Metadata, Passage, UserMetadataSchema, UserMetadataType } from '@passageidentity/passage-js'
import dayjs from 'dayjs'
import { ref } from 'vue'

export function transformMetadata(metadata: Metadata, schema: UserMetadataSchema[], defaultBooleans = false): Metadata {
  const transformedMetadata = { ...metadata }
  schema.forEach((schema) => {
    const metadataValue = transformedMetadata[schema.field_name]
    if (schema.type === UserMetadataType.DATE && metadataValue) {
      transformedMetadata[schema.field_name] = dayjs(metadataValue as string).format('YYYY-MM-DD')
    }
    if (defaultBooleans && schema.type === UserMetadataType.BOOLEAN && metadataValue === undefined) {
      transformedMetadata[schema.field_name] = false
    }
  })
  return transformedMetadata
}

/* eslint-disable-next-line @typescript-eslint/explicit-function-return-type, @typescript-eslint/explicit-module-boundary-types*/
export default function useUpdateUser(appID: string) {
  const { t } = useLocale()
  const passage = new Passage(appID)
  const passageUser = passage.getCurrentUser()
  const updateInfoMessage = ref<string>('')
  const updateEmailError = ref<string>('')
  const updatePhoneError = ref<string>('')
  const updateMetadataError = ref<string>('')
  const updatePending = ref<boolean>(false)

  const identifierExists = async (identifier: string): Promise<boolean> => {
    updatePending.value = true
    const existingUser = await passage.identifierExists(identifier)
    updatePending.value = false
    if (existingUser) {
      return false
    }
    return true
  }

  const changeEmailRequest = async (newEmail: string): Promise<void> => {
    updatePending.value = true
    try {
      await passageUser.changeEmail(newEmail)
      if (newEmail) {
        updateInfoMessage.value = updateInfoMessage.value = t('weve-sent-confirmation-email')
      }
    } catch (e) {
      updateEmailError.value =
        newEmail === '' ? t('your-account-must-have-either-an-email-or-a-phone') : t('failed-to-update-email')
    } finally {
      updatePending.value = false
    }
  }

  const changePhoneRequest = async (newPhone: string): Promise<void> => {
    updatePending.value = true
    try {
      await passageUser.changePhone(newPhone)
      if (newPhone) {
        updateInfoMessage.value = t('weve-sent-confirmation-text')
      }
    } catch (e) {
      updatePhoneError.value =
        newPhone === '' ? t('your-account-must-have-either-an-email-or-a-phone') : t('failed-to-update-phone')
    } finally {
      updatePending.value = false
    }
  }

  const updateMetadata = async (newMetadata: Metadata, schema: UserMetadataSchema[]): Promise<boolean> => {
    updatePending.value = true
    try {
      const transformedMetadata = transformMetadata(newMetadata, schema)
      await passageUser.updateMetadata(transformedMetadata)
      return true
    } catch (e) {
      updateMetadataError.value = t('failed-to-update-metadata')
      return false
    } finally {
      updatePending.value = false
    }
  }

  return {
    changeEmailRequest,
    updateEmailError,
    changePhoneRequest,
    updatePhoneError,
    updatePending,
    updateInfoMessage,
    identifierExists,
    updateMetadata,
    updateMetadataError,
  }
}
import { onMounted, Ref } from 'vue'

export function useIntlTelInput(containerRef: Ref): void {
  // Intl-tel-input styles must be loaded as a link attached to the shadowDOM
  onMounted(() => {
    if (containerRef.value === null) {
      return
    }
    const shadowDOM = (containerRef.value as HTMLElement).parentNode
    if (shadowDOM === null) {
      return
    }
    const link = document.createElement('link')
    link.rel = 'stylesheet'
    link.href = 'https://cdnjs.cloudflare.com/ajax/libs/intl-tel-input/17.0.16/css/intlTelInput.css'
    shadowDOM.appendChild(link)
  })
}
import { VueElementConstructor } from '@vue/runtime-dom'

export function safelyRegisterElement(
  vueComponent: VueElementConstructor<Record<string, unknown>>,
  elementName: string,
): void {
  try {
    if (!window.customElements.get(`passage-${elementName}`)) {
      window.customElements.define(`passage-${elementName}`, vueComponent)
    }
  } catch {
    throw new Error(
      `Cannot register custom element <passage-${elementName}>. Ensure the import is happening while code is running client-side in the browser. If doing server-side rendering consider moving the import to a lifecycle event. See https://docs.passage.id/ for more information.`,
    )
  }
}
