/**
 * BytesHelper assists in encoding and decoding bytes
 */
export default class BytesHelper {
    /**
     * Encode bytes using array buffer
     * @param {ArrayBufferLike} arrayBuffer The array buffer
     * @return {string} Base64 URL string
     */
    encodeBytes(arrayBuffer: ArrayBufferLike): string {
        const b64Str = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)));
        const b64URLStr = b64Str.replace(/\+/g, '-').replace(/\//g, '_');
        return removePadding(b64URLStr);
    }

    /**
     * Decode bytes using a Base64 URL string
     * @param {string} b64URLStr The Base64 URL string
     * @return {Uint8Array} Array buffer
     */
    decodeBytes(b64URLStr: string): Uint8Array {
        const b64Str = b64URLStr.replace(/-/g, '+').replace(/_/g, '/');
        const b64StrWithoutPadding = removePadding(b64Str);
        const arrayBuffer = Uint8Array.from(atob(b64StrWithoutPadding), (c) => c.charCodeAt(0));
        return arrayBuffer;
    }
}

const removePadding = (s: string): string => {
    const padIdx = s.indexOf('=');
    return s.slice(0, padIdx === -1 ? s.length : padIdx);
};
