        const psgCredID = JSON.parse(psgCredObj)[userID];
        if (!psgCredID) {
            return false;
        }
        const psgCredIdExists: boolean = allowCredentials.find((credential: Credential) => {
            if (
                bytesHelper.encodeBytes(bytesHelper.decodeBytes(credential.id)) ===
                bytesHelper.encodeBytes(bytesHelper.decodeBytes(psgCredID))
            ) {
                return true;
            }
        });

        return psgCredIdExists;
    }

