     *
     * @param {string} userID The Passage userID to be used as the key in psg_cred_obj
     * @param {Credential | null} credential The credential to be associated with the userID in localstorage
     * as the value associated with the userID key in psg_cred_obj
     */
    setPsgCredID(userID: string, credential: Credential | null): void {
        if (!credential)
            throw new PassageError(`Could not get user's credential`, {
                status: PassageErrorCode.PSGCouldNotGetUserCredential,
                statusText: 'PSG Could Not Get User Credential',
            });

        const passageCredentialObject = this.getPsgCredObj();

        if (passageCredentialObject) {
            const parsedCredentialObject: { [key: string]: string } =
                JSON.parse(passageCredentialObject);
            parsedCredentialObject[userID] = credential?.id!;
            this.setPsgCredObj(JSON.stringify(parsedCredentialObject));
        } else {
            this.setPsgCredObj(JSON.stringify({ [userID]: credential?.id }));
        }
    }
