     * if found in cookies, extend expiration
     * @return {string | undefined} the psg_cred_obj string or undefined
     */
    getPsgCredObj(): string | undefined {
        const cookieCredObj = Cookies.get('psg_cred_obj');
        const localStorageCredObj = localStorage.getItem('psg_cred_obj');
        if (cookieCredObj !== undefined) {
            // extend the cookie's expiration
            Cookies.set('psg_cred_obj', cookieCredObj, { expires: 400, sameSite: 'Strict' });
            // mirror back to localstorage
            if (localStorageCredObj === null) {
                localStorage.setItem('psg_cred_obj', cookieCredObj);
            }
            return cookieCredObj;
        }
        if (localStorageCredObj !== null) {
            // mirror to cookies
            Cookies.set('psg_cred_obj', localStorageCredObj, { expires: 400, sameSite: 'Strict' });
