        }
        return undefined;
    }
    /**
     * Sets psg_cred_obj on cookies and localstorage
     * @param {string} value The string to store in psg_cred_obj
     */
    private setPsgCredObj(value: string): void {
        Cookies.set('psg_cred_obj', value, { expires: 400, sameSite: 'Strict' });
        localStorage.setItem('psg_cred_obj', value);
    }
