    private _parsePsgErrorResponse(psgErrorResponse: any): string {
        try {
            psgErrorResponse = JSON.parse(psgErrorResponse);
            if (psgErrorResponse.error) {
                return psgErrorResponse.error;
            }
            return '';
        } catch {
            return psgErrorResponse;
        }
    }
}

