/**
 * BrowserHelper assists in detecting what features the browser supports
 */
export default class BrowserHelper {
    userAgent: UaParser.UAParserInstance;

    /**
     * Constructor configuring the CredentialHelper class' attributes
     */
    constructor() {
        this.userAgent = new UaParser();
    }

    /**
     * Detect if Browser can create a PassKey
     * @return {boolean}
     */
    async getBrowserInfo(): Promise<BrowserInfo> {
        return {
            createPassKeySupported: await this._canBrowserCreatePassKey(),
            getPassKeySupported: await this._canBrowserGetPasskey(),
            conditionalUISupported: await this._isConditionalMediationAvailable(),
        };
    }

    /**
     * Detect if Browser can create a PassKey
     * @return {boolean}
     */
    private async _canBrowserCreatePassKey(): Promise<boolean> {
        // iOS Supported
        if (this._isiOSPasskeySupported()) {
            return true;
        }

        // MacOS Safari Supported
        if (this._isMacPasskeySupported()) {
            return true;
        }

        return false;
    }

    /**
     * Detect if Browser can use Passkeys
     * @return {boolean}
     */
    private _canBrowserGetPasskey(): Promise<boolean> {
        // iOS Supported
        if (this._isiOSPasskeySupported()) {
            return Promise.resolve(true);
        }

        // MacOS Safari Supported
        if (this._isMacPasskeySupported()) {
            return Promise.resolve(true);
        }

        if (this._isDesktopChrome108()) {
            return Promise.resolve(true);
        }

        return Promise.resolve(false);
    }

    /**
     * Detect if Browser has implemented Conditional Mediation
     * @return {boolean}
     */
    private async _isConditionalMediationAvailable(): Promise<boolean> {
        return false;
        /*
        // Running this code on iOS breaks user gestures entirely
        if (!PublicKeyCredential.isConditionalMediationAvailable) {
            return false;
        }

        return await PublicKeyCredential.isConditionalMediationAvailable();
        */
    }

    /**
     * Detect if Browser is running on iOS16 or greater
     * @return {boolean}
     */
    private _isiOSPasskeySupported(): boolean {
        return (
            this.userAgent.getOS().name == 'iOS' &&
            parseFloat(this.userAgent.getOS().version || '') >= 16
        );
    }

    /**
     * Detect if Browser is running MacOS and Safari 16 or greater
     * @return {boolean}
     */
    private _isMacPasskeySupported(): boolean {
        return (
            this.userAgent.getBrowser().name == 'Safari' &&
            parseFloat(this.userAgent.getBrowser().version || '') >= 16 &&
            this.userAgent.getOS().name == 'Mac OS'
        );
    }

    /**
     * Detect if Browser is running Chrome 108 desktop or greater
     * @return {boolean}
     */
    private _isDesktopChrome108(): boolean {
        return (
            this.userAgent.getBrowser().name == 'Chrome' &&
            parseFloat(this.userAgent.getBrowser().version || '') >= 108
        );
    }
}
