 * @return {Promise<PassageError | any>}
 */
export async function handleResponse(res: Response): Promise<PassageError | any> {
    if (!res.ok) {
        return await res.text().then((psgErrorResponse) => {
            throw new PassageError(psgErrorResponse, res);
        });
    }
    return res.json();
}
