type AuthResponse = {
    auth_result: authResult;
};

const isAuthResponse = (res: unknown): res is AuthResponse => {
    return (
        typeof res === 'object' &&
        res !== null &&
        typeof (res as AuthResponse).auth_result !== 'undefined'
    );
};

/**
 * storeRefreshToken stores a refresh token from a response body if possible
 * @param {unknown} res The response from the request
 * @param {TokenStore} tokenStore token Store being used
 * @return {unknown}
 */
export function storeResponseTokens<T = unknown>(
    res: T,
    tokenStore: TokenStore | ReadOnlyTokenStore
): T {
    if (!isAuthResponse(res)) {
        console.warn('Not a valid auth response');
        return res;
    }
    if (isFullTokenStore(tokenStore)) {
        tokenStore.setTokens(res.auth_result);
    }
    return res;
}
