/* eslint-disable prefer-promise-reject-errors */
export class PassageTokenStore extends TokenStore {
    private noLocalStorage = typeof window === 'undefined';
    private noLocalStorageError = (f: string): string =>
        `Failed to access localStorage or cookies. Must be run client-side.`;

    getAuthToken(): Promise<string> {
        if (this.noLocalStorage) {
            return Promise.reject(this.noLocalStorageError('getAuthToken'));
        }
        const localToken = localStorage.getItem('psg_auth_token');
        if (localToken === null) {
            const err = new PassageError('Login required', {
                status: PassageErrorCode.PSGLoginRequired,
                statusText: 'Login required',
            });
            return Promise.reject(err);
        }
        return Promise.resolve(localToken);
    }

    private setAuthToken(authToken: string): Promise<void> {
        if (this.noLocalStorage) {
            return Promise.reject(this.noLocalStorageError('setAuthToken'));
        }
        localStorage.setItem('psg_auth_token', authToken);
        const leaveUnsecure = !this._isHttps() && this._isLocalHost();
        document.cookie = `psg_auth_token = ${authToken}; path=/ ${leaveUnsecure ? '' : ';secure'}`;
        return Promise.resolve();
    }

    getRefreshToken(): Promise<string | undefined> {
        if (this.noLocalStorage) {
            Promise.reject(this.noLocalStorageError('getRefreshToken'));
        }
        const localToken = localStorage.getItem('psg_refresh_token');
        if (localToken === null) {
            return Promise.resolve(undefined);
        }
        return Promise.resolve(localToken);
    }

    setRefreshToken(refreshToken: string): Promise<void> {
        if (this.noLocalStorage) {
            Promise.reject(this.noLocalStorageError('setRefreshToken'));
        }
        localStorage.setItem('psg_refresh_token', refreshToken);
        return Promise.resolve();
    }

    setTokens(authResult: authResult): Promise<void> {
        if (this.noLocalStorage) {
            Promise.reject(this.noLocalStorageError('setTokens'));
        }
        this.setAuthToken(authResult.auth_token);
        if (authResult.refresh_token) {
            this.setRefreshToken(authResult.refresh_token);
        }
        return Promise.resolve();
    }

    clearTokens(): Promise<void> {
        if (this.noLocalStorage) {
            Promise.reject(this.noLocalStorageError('clearTokens'));
        }
        localStorage.removeItem('psg_auth_token');
        document.cookie = 'psg_auth_token=; expires=Thu, 01 Jan 1970 00:00:00 UTC;';
        localStorage.removeItem('psg_refresh_token');
        return Promise.resolve();
    }

    /**
     * Determines if localhost
     * @return {boolean} The sum of the two numbers.
     */
    private _isLocalHost(): boolean {
        return Boolean(
            window.location.hostname === 'localhost' ||
                // [::1] is the IPv6 localhost address.
                window.location.hostname === '[::1]' ||
                // 127.0.0.1/8 is considered localhost for IPv4.
                window.location.hostname.match(
                    /^127(?:\.(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)){3}$/
                )
        );
    }

    /**
     * Determines if protocol being used is HTTPS
     * @return {boolean}
     */
    private _isHttps(): boolean {
        return location.protocol === 'https:';
    }
}

export function isFullTokenStore(store: ReadOnlyTokenStore | TokenStore): store is TokenStore {
    return (store as TokenStore).setTokens !== undefined;
}
