const bytesHelper = new BytesHelper();
// const authTokenHelper = new AuthTokenHelper();
declare const navigator: Navigator;

/**
 * LogIn assists with authenticating Passage Users via WebAuthn
 */
export default class LogIn {
    apiUrl: string;
    identifier: string;

    /**
     * LogIn constructor initializing apiURL and identifier class vars
     * @param {string} baseUrl
     * @param {strting} identifier
     */
    constructor(
        baseUrl: string,
        private tokenStore: TokenStore | ReadOnlyTokenStore,
        identifier?: string
    ) {
        this.apiUrl = baseUrl + '/login/';
        this.identifier = identifier ?? '';
    }

    /**
     * Begin authentication via WebAuthn
     * @return {Promise<authResult>}
     */
    async withWebAuthn(): Promise<authResult> {
        const identifierBody = JSON.stringify({
            identifier: this.identifier,
        });
        const body = this.identifier ? identifierBody : undefined;
        const webAuthnStartResponse = await fetch(this.apiUrl + 'webauthn/start', {
            headers: getHeaders(),
            method: 'POST',
            body,
        }).then(handleResponse);

        /* const psgCredIDExists = await authTokenHelper.psgCredIDExists(webAuthnStartResponse);
        if (!psgCredIDExists) {
            throw new PassageError('psg_cred_obj does not exist in the allow list', {
                status: PassageErrorCode.PSGCredObjectDoesNotExist,
                statusText: 'psg_cred_obj does not exist in the allow list',
            });
        } */

        const assertion: PublicKeyCredential | null = await navigator.credentials
            .get({
                publicKey: this._parsePublicKey(
                    webAuthnStartResponse.handshake.challenge.publicKey
                ),
            })
            .catch((err) => {
                throw new PassageError('error parsing public key for webAuthn', {
                    status: PassageErrorCode.PSGParsePublicKeyForLoginFailed,
                    statusText: 'PSG Parse Public Key For Login Failed',
                });
            })
            .then((credValue) => {
                return credValue as PublicKeyCredential;
            });

        if (!assertion) {
            return {
                auth_token: '',
                redirect_url: '',
            };
        }
        const webAuthnFinishResponse = await fetch(this.apiUrl + 'webauthn/finish', {
            headers: getHeaders(),
            method: 'POST',
            body: JSON.stringify({
                user_id: this.identifier ? webAuthnStartResponse.user.id : undefined,
                handshake_id: webAuthnStartResponse.handshake.id,
                handshake_response: {
                    id: assertion.id,
                    rawId: bytesHelper.encodeBytes(assertion.rawId),
                    type: assertion.type,
                    response: {
                        clientDataJSON: bytesHelper.encodeBytes(assertion.response.clientDataJSON),
                        authenticatorData: bytesHelper.encodeBytes(
                            // @ts-ignore
                            assertion.response.authenticatorData
                        ),
                        // @ts-ignore
                        signature: bytesHelper.encodeBytes(assertion.response.signature),
                        // @ts-ignore
                        userHandle: bytesHelper.encodeBytes(assertion.response.userHandle),
                    },
                },
            }),
        })
            .then(handleResponse)
            .then((response) => storeResponseTokens(response, this.tokenStore));

        // @ts-ignore
        // const userID = String.fromCharCode(...new Uint8Array(assertion.response.userHandle));
        // authTokenHelper.setPsgCredID(userID, assertion);
        return webAuthnFinishResponse.auth_result;
    }

    /**
     * Parse the public key via credential request options
     * @param {PublicKeyCredentialRequestOptions} publicKey
     * @return {PublicKeyCredentialRequestOptions}
     */
    private _parsePublicKey(
        publicKey: PublicKeyCredentialRequestOptions
    ): PublicKeyCredentialRequestOptions {
        publicKey.challenge = bytesHelper.decodeBytes(publicKey.challenge.toString());
        if (publicKey.allowCredentials) {
            for (let i = 0; i < publicKey.allowCredentials.length; i++) {
                const e = publicKey.allowCredentials[i];
                e.id = bytesHelper.decodeBytes(e.id.toString());
            }
            return publicKey;
        }
        return publicKey;
    }
}
