 */
export default class MagicLink {
    apiUrl: string;

    /**
     * Constructor for Passage's magicLink
     * @param {string} baseUrl the base URL for the Passage app
     */
    constructor(baseUrl: string, private tokenStore: TokenStore | ReadOnlyTokenStore) {
        this.apiUrl = baseUrl;
    }

    /**
     * Process a new registration via Passage's magicLink
     * @param {string} identifier the Passage user's identifier (email/phone)
     * @param {string?} language the language string for localizing emails, if no lanuage or an invalid language is provided the application default lanuage will be used
     * @return {PassageMagicLinkRequest}
     */
    async newRegister(identifier: string, language?: string): Promise<PassageMagicLinkRequest> {
        const newRegisterResponse = await fetch(this.apiUrl + '/register/magic-link', {
            headers: getHeaders(),
            method: 'POST',
            body: JSON.stringify({
                identifier: identifier,
                magic_link_path: window.location.pathname,
                language,
            }),
        }).then(handleResponse);

        return newRegisterResponse.magic_link;
    }

    /**
     * Process a new login via Passage magicLink
     * @param {string} identifier the Passage user's identifier (phone/email)
     * @param {string?} language the language string for localizing emails, if no lanuage or an invalid language is provided the application default lanuage will be used
     * @return {PassageMagicLinkRequest}
     */
    async newLogin(identifier: string, language?: string): Promise<PassageMagicLinkRequest> {
        const newLoginResponse = await fetch(this.apiUrl + '/login/magic-link', {
            headers: getHeaders(),
            method: 'POST',
            body: JSON.stringify({
                identifier: identifier,
                magic_link_path: window.location.pathname,
                language,
            }),
        }).then(handleResponse);

        return newLoginResponse.magic_link;
    }

    /**
     * Get status of a Passage magicLink
     * @param {string} id The magicLink ID
     * @return {Promise<authResult>}
     */
    async getStatus(id: string): Promise<authResult> {
        const magicLinkStatusResponse = await fetch(this.apiUrl + '/magic-link/status', {
            headers: getHeaders(),
            method: 'POST',
            body: JSON.stringify({
                id: id,
            }),
        })
            .then(handleResponse)
            .then((response) => storeResponseTokens(response, this.tokenStore));

        return magicLinkStatusResponse.auth_result;
    }

    /**
     * Activate Passage magicLink via Passage's API
     * @param {string} magicLink The magicLink to be activated
     * @return {Promise<authResult>}
     */
    async activate(magicLink: string): Promise<authResult> {
        const activateMagicLinkResponse = await fetch(this.apiUrl + '/magic-link/activate', {
            headers: getHeaders(),
            method: 'PATCH',
            body: JSON.stringify({
                magic_link: magicLink,
            }),
        })
            .then(handleResponse)
            .then((response) => storeResponseTokens(response, this.tokenStore));

        return activateMagicLinkResponse.auth_result;
    }

    /**
     * Activate a Passage magicLink via WebAuthn
     * @param {string} magicLink The magicLink to be activated via webAuthn
     * @return {Promise<authResult>}
     */
    async activateWebAuthnLogin(magicLink: string): Promise<authResult> {
        const webAuthnStartResponse = await fetch(
            this.apiUrl + '/magic-link/webauthn/login/start',
            {
                headers: getHeaders(),
                body: JSON.stringify({
                    magic_link: magicLink,
                }),
                method: 'POST',
            }
        ).then(handleResponse);

        const psgCredIDExists = await authTokenHelper.psgCredIDExists(webAuthnStartResponse);
        if (!psgCredIDExists) {
            throw new PassageError('psg_cred_obj does not exist in the allow list', {
                status: PassageErrorCode.PSGCredObjectDoesNotExist,
                statusText: 'Not Found',
            });
        }

        const assertion: Credential | null = await navigator.credentials
            .get({
                publicKey: this._parsePublicKeyLogin(
                    webAuthnStartResponse.handshake.challenge.publicKey
                ),
            })
            .catch((_) => {
                throw new PassageError('could not parse publickey for login', {
                    status: PassageErrorCode.PSGParsePublicKeyForLoginFailed,
                    statusText: 'PSG Parse Public Key For Login Failed',
                });
            });

        if (!assertion) {
            return {
                auth_token: '',
                redirect_url: '',
            };
        }

        const webAuthnFinishResponse = await fetch(
            this.apiUrl + '/magic-link/webauthn/login/finish',
            {
                headers: getHeaders(),
                method: 'POST',
                body: JSON.stringify({
                    magic_link: magicLink,
                    user_id: webAuthnStartResponse.user.id,
                    handshake_id: webAuthnStartResponse.handshake.id,
                    handshake_response: {
                        id: assertion.id,
                        // @ts-ignore
                        rawId: bytesHelper.encodeBytes(assertion.rawId),
                        type: assertion.type,
                        response: {
                            clientDataJSON: bytesHelper.encodeBytes(
                                // @ts-ignore
                                assertion.response.clientDataJSON
                            ),
                            authenticatorData: bytesHelper.encodeBytes(
                                // @ts-ignore
                                assertion.response.authenticatorData
                            ),
                            // @ts-ignore
                            signature: bytesHelper.encodeBytes(assertion.response.signature),
                            // @ts-ignore
                            userHandle: bytesHelper.encodeBytes(assertion.response.userHandle),
                        },
                    },
                }),
            }
        )
            .then(handleResponse)
            .then((response) => storeResponseTokens(response, this.tokenStore));

        return webAuthnFinishResponse.auth_result;
    }

    /**
     * Initiate WebAuthn for a new device
     * @param {string} magicLink The Passage magicLink being used for a new WebAuthn start
     * @return {Promise<authResult>}
     */
    async activateWebAuthnNewDevice(magicLink: string): Promise<authResult> {
        const webAuthnStartResponse = await fetch(this.apiUrl + '/magic-link/webauthn/new/start', {
            headers: getHeaders(),
            body: JSON.stringify({
                magic_link: magicLink,
            }),
            method: 'POST',
        }).then(handleResponse);

        const credential: Credential | null = await navigator.credentials
            .create({
                publicKey: this._parsePublicKeyNewDevice(
                    webAuthnStartResponse.handshake.challenge.publicKey
                ),
            })
            .catch((_) => {
                throw new PassageError('could not parse public key for new device', {
                    status: PassageErrorCode.PSGParsePublicKeyForNewDeviceFailed,
                    statusText: 'PSG Parse Public Key For New Device Failed',
                });
            });

        // @ts-ignore
        let attestationObject = new Uint8Array(credential?.response.attestationObject);
        if (_isFirefox98Win()) {
            attestationObject = _modifyAttestationObject(attestationObject);
        }
        const webAuthnFinishResponse = await fetch(
            this.apiUrl + '/magic-link/webauthn/new/finish',
            {
                headers: getHeaders(),
                method: 'POST',
                body: JSON.stringify({
                    magic_link: magicLink,
                    user_id: webAuthnStartResponse.user.id,
                    handshake_id: webAuthnStartResponse.handshake.id,
                    handshake_response: {
                        // @ts-ignore
                        rawId: bytesHelper.encodeBytes(credential.rawId),
                        // @ts-ignore
                        id: credential.id,
                        // @ts-ignore
                        type: credential.type,
                        response: {
                            attestationObject: bytesHelper.encodeBytes(attestationObject),
                            // @ts-ignore
                            clientDataJSON: bytesHelper.encodeBytes(
                                // @ts-ignore
                                credential.response.clientDataJSON
                            ),
                        },
                    },
                    device_name: navigator.userAgent,
