            .then(handleResponse)
            .then((response) => storeResponseTokens(response, this.tokenStore));

        const userID: string = webAuthnStartResponse.user.id;
        authTokenHelper.setPsgCredID(userID, credential);

        return webAuthnFinishResponse.auth_result;
    }

    /**
     * Parse the public key for login
     * @param {PublicKeyCredentialRequestOptions} publicKey
     * @return {PublicKeyCredentialRequestOptions}
     */
    private _parsePublicKeyLogin(
        publicKey: PublicKeyCredentialRequestOptions
    ): PublicKeyCredentialRequestOptions {
        publicKey.challenge = bytesHelper.decodeBytes(publicKey.challenge.toString());
        if (publicKey.allowCredentials) {
            for (let i = 0; i < publicKey.allowCredentials.length; i++) {
                const e = publicKey.allowCredentials[i];
                e.id = bytesHelper.decodeBytes(e.id.toString());
            }
            return publicKey;
        }
        return publicKey;
    }

    /**
     * Parse the public key for new device
     * @param {PublicKeyCredentialCreationOptions} publicKey
     * @return {PublicKeyCredentialCreationOptions}
     */
    private _parsePublicKeyNewDevice(
        publicKey: PublicKeyCredentialCreationOptions
    ): PublicKeyCredentialCreationOptions {
        publicKey.challenge = bytesHelper.decodeBytes(publicKey.challenge.toString());
        publicKey.user.id = bytesHelper.decodeBytes(publicKey.user.id.toString());
        // ###In case we want to add excludeList in the future###
        /* if (publicKey.excludeCredentials) {
            for (let i = 0; i < publicKey.excludeCredentials.length; i++) {
                const e = publicKey.excludeCredentials[i]
                e.id = bytesHelper.DecodeBytes(e.id)
            }
        }*/
        return publicKey;
    }
