    }

    /**
     * changePhone initiates a phone number change for the authenticated user. An phone number change requires verification, so an SMS with a link will be sent to the user which they must verify before the phone number change takes effect.
     * @param {string} newPhone
     * @param {string?} language the language string for localizing emails, if no lanuage or an invalid language is provided the application default lanuage will be used
     * @return {PassageMagicLinkRequest} the magicLink response on success.
     */
    async changePhone(newPhone: string, language?: string): Promise<PassageMagicLinkRequest> {
        const token = await this._getToken();
        const changePhoneResponse = await fetch(`${this.apiUrl}/phone`, {
            method: 'PATCH',
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json',
            }),
            body: JSON.stringify({
                new_phone: newPhone,
                language,
            }),
        }).then(handleResponse);

        return changePhoneResponse.magic_link;
    }

    /**
     * editDevice is used to edit the current user's device attributes such as device_name
     * @param {string} deviceID
     * @param {PassageEditDeviceRequest} editDeviceRequest
     * @return {PassageDevice} the edited device after applying the requested device attribute changes.
     */
    async editDevice(
        deviceID: string,
        editDeviceRequest: PassageEditDeviceRequest
    ): Promise<PassageDevice> {
        if (deviceID === '' || deviceID === undefined) {
            throw new PassageError('A deviceID is required for an edit device request.', {
                status: PassageErrorCode.BadRequest,
                statusText: 'Bad Request',
            });
        }

        if (Object.keys(editDeviceRequest).length === 0) {
            throw new PassageError('Edit device request must not have an empty request body.', {
                status: PassageErrorCode.BadRequest,
                statusText: 'Bad Request',
            });
        }

        const token = await this._getToken();
        const passageDeviceResponse = await fetch(`${this.apiUrl}/devices/${deviceID}`, {
            method: 'PATCH',
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json',
            }),
            body: JSON.stringify({
                ...editDeviceRequest,
            }),
        }).then(handleResponse);

        return passageDeviceResponse.device;
    }

    /**
     * listDevices is used to list the current user's devices.
     * @param {string} deviceID
     * @param {PassageEditDeviceRequest} editDeviceRequest
     * @return {PassageDevice[]} the array of devices.
     */
    async listDevices(): Promise<PassageDevice[]> {
        const token = await this._getToken();
        const deviceResponse = await fetch(`${this.apiUrl}/devices/`, {
            method: 'GET',
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json',
            }),
        }).then(handleResponse);

        return deviceResponse.devices;
    }

    /**
     * addDevice is used to add a new webAuthn device for the current user.
     * @return {PassageDevice} the new webAuthn device registered with Passage.
     */
    async addDevice(): Promise<PassageDevice> {
        const token = await this._getToken();
        const { createPassKeySupported } = await browserHelper.getBrowserInfo();
        const webAuthnStartResponse = await fetch(this.apiUrl + '/devices/start', {
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
            }),
            method: 'POST',
        }).then(handleResponse);

        const credential: PublicKeyCredential | null = await navigator.credentials
            .create({
                // @ts-ignore
                publicKey: this._parsePublicKey(
                    webAuthnStartResponse.handshake.challenge.publicKey
                ),
            })
            .catch((_) => {
                throw new PassageError('failed to parse public key', {
                    status: PassageErrorCode.PSGParsePublicKeyForNewDeviceFailed,
                    statusText: 'PSG Parse Public Key For New Device Failed',
                });
            })
            .then((credValue) => {
                return credValue as PublicKeyCredential;
            });

        let transports: string[] = [];
        // getTransports() is not defined in all browsers (i.e. Firefox)
        // make sure the function is defined before trying to use it
        if (
            // @ts-ignore
            credential?.response.getTransports &&
            // @ts-ignore
            typeof credential?.response.getTransports === 'function'
        ) {
            // @ts-ignore
            transports = credential?.response.getTransports();
        }
        const userID = webAuthnStartResponse.user.id;
        const webAuthnFinishResponse = await fetch(this.apiUrl + '/devices/finish', {
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
            }),
            method: 'POST',
            body: JSON.stringify({
                user_id: userID,
                handshake_id: webAuthnStartResponse.handshake.id,
                handshake_response: {
                    rawId: bytesHelper.encodeBytes(credential?.rawId!),
                    id: credential?.id,
                    type: credential?.type,
                    response: {
                        attestationObject: bytesHelper.encodeBytes(
                            // @ts-ignore
                            credential?.response.attestationObject
                        ),

                        clientDataJSON: bytesHelper.encodeBytes(
                            credential?.response.clientDataJSON!
                        ),
                    },
                    transports: transports,
                },
                cred_type: createPassKeySupported ? 'passkey' : 'platform',
            }),
        }).then(handleResponse);

        authTokenHelper.setPsgCredID(userID, credential);
        return webAuthnFinishResponse.device;
    }

    /**
     * deleteDevice deletes an existing webAuthn device for the current user.
     * @param {string | PassageDevice} deviceID the ID of the device to be deleted or a PassageDevice object
     * @return {boolean} true if the device has been deleted successfully.
     */
    async deleteDevice(deviceID: string | PassageDevice): Promise<boolean> {
        // we do this type check for backwards compatibility
        // old versions of passage-js 3.0.1 and earlier only accepted a deviceID string
        // To support updating the psg_cred_obj we added support for passing a PassageDevice object as well
        const typedID = typeof deviceID === 'string' ? deviceID : deviceID.id;
        const credID = typeof deviceID === 'string' ? undefined : deviceID.cred_id;
        if (!typedID) {
            throw new PassageError('A deviceID is required to delete a device.', {
                status: PassageErrorCode.NotFound,
                statusText: 'Not Found',
            });
        }
        let deviceResponse = false;
        const token = await this._getToken();
        deviceResponse = await fetch(`${this.apiUrl}/devices/${typedID}`, {
            method: 'DELETE',
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json',
            }),
        }).then(async (res) => {
            if (!res.ok) {
                return await res.text().then((psgErrorResponse) => {
                    throw new PassageError(psgErrorResponse, res);
                });
            }
            return true;
        });

        // if the remove was successful and we know the matching credID, remove from psg_cred_obj
        if (deviceResponse && credID !== undefined) {
            authTokenHelper.removeCredential(credID);
        }
        return deviceResponse;
    }

    /**
     * getMetadata returns the metadata for the currently authenticated user.
     * @return {Metadata | undefined} the current Passage user's info, or undefined if the
     * current Passage user's authentication token could not be validated.
     */
    async getMetadata(): Promise<Metadata | undefined> {
        const token = await this._getToken();
        const metadataResponse = await fetch(`${this.apiUrl}/user-metadata`, {
            method: 'GET',
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
            }),
        }).then(handleResponse);

        return metadataResponse.user_metadata;
    }

    /**
     * updateMetadata updates the metadata for the currentuser. Note that only metadata fields set for the app can be updated.
     * @param {Metadata} metadata
     * @return {PassageUser} the current user with their newly updated metadata field.
     */
    async updateMetadata(metadata: Metadata): Promise<PassageUser> {
        const token = await this._getToken();
        const metadataUpdateResponse = await fetch(`${this.apiUrl}/user-metadata`, {
            method: 'PATCH',
            headers: getHeaders({
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json',
            }),
            body: JSON.stringify({
                user_metadata: metadata,
            }),
        }).then(handleResponse);

        return metadataUpdateResponse.user;
    }

    /**
     * Parse the public key via credential creation options.
     * @param {PublicKeyCredentialCreationOptions} publicKey
     * @return {PublicKeyCredentialCreationOptions}
     */
    private _parsePublicKey(
        publicKey: PublicKeyCredentialCreationOptions
    ): PublicKeyCredentialCreationOptions {
        publicKey.challenge = bytesHelper.decodeBytes(publicKey.challenge.toString());
        publicKey.user.id = bytesHelper.decodeBytes(publicKey.user.id.toString());
        // ###In case we want to add excludeList in the future###
        /* if (publicKey.excludeCredentials) {
            for (let i = 0; i < publicKey.excludeCredentials.length; i++) {
                const e = publicKey.excludeCredentials[i]
                e.id = bytesHelper.DecodeBytes(e.id)
            }
        }*/
        return publicKey;
    }

    /**
     * Returns the external token if used or gets the current auth token, and performs a refresh operation if necessary.
     * @return {Promise<string>}
     */
    private async _getToken(): Promise<string> {
        return await this.session.getAuthToken();
    }
}
import jwtDecode, { JwtPayload } from 'jwt-decode';
import { PassageError, PassageErrorCode } from './PassageError';
import { getHeaders } from './utils/GetHeaders';
import { handleResponse } from './utils/HandleResponse';
import { authResult } from './Passage';
import { ReadOnlyTokenStore, TokenStore, isFullTokenStore } from './TokenStore';
import { getBaseURL } from './utils/getBaseURL';

interface JwtHeader {
    alg: string;
    typ: string;
}

/**
 * The Session class used to manage Passage sessions using refresh tokens.
 */
export class Session {
    private apiUrl: string;

    /**
     * Session constructor initializing apiURL and identifier class vars
     * @param {string} [appID] the App's corresponding AppID
     * @param {ReadOnlyTokenStore | TokenStore} [tokenStore] A tokenStore object that enables getting Passage User information from an externally stored token.
     * If the tokenStore implements the full TokenStore class, the Session will handle refreshing Passage sessions via the refresh token.
     */
    constructor(appID: string, private tokenStore: TokenStore | ReadOnlyTokenStore) {
        this.apiUrl = getBaseURL(appID) + '/tokens/';
    }

    /**
     * authGuard checks if the current user has a JWT that is valid and potentially verifiable.
     * IMPORTANT: this is not verifying the JWT, it is only checking if the JWT is formatted properly.
     * @return {boolean} true if the user has a valid (but unverified) JWT, false if not.
     */
    async authGuard(): Promise<boolean> {
        try {
            const authToken = await this.tokenStore.getAuthToken();
            const header = jwtDecode<JwtHeader>(authToken, { header: true });
            const payload = jwtDecode<JwtPayload>(authToken);
            if (payload !== undefined && header !== undefined) {
                if (this._validJWTPayload(payload) && this._validJWTHeader(header)) {
                    return true;
                }
            }
            return false;
        } catch (e) {
            return false;
        }
    }

    /**
     * Sign out a user by deleting their AuthToken from local storage and revoking their refresh token.
     * @return {boolean} true if the device has been deleted successfully.
     */
    async signOut(): Promise<boolean> {
        const refreshToken = await this.tokenStore.getRefreshToken();
        if (!refreshToken) {
            if (isFullTokenStore(this.tokenStore)) {
                this.tokenStore.clearTokens();
            }
            return true;
        }
        return fetch(`${this.apiUrl}?` + new URLSearchParams({ refresh_token: refreshToken }), {
            method: 'DELETE',
        })
            .then(({ status }) => status === 200)
            .finally(() => {
                if (isFullTokenStore(this.tokenStore)) {
                    this.tokenStore.clearTokens();
                }
            });
    }

    /**
     * getAuthToken gets the current authToken for the currently authenticated user.
     * @return {string} returns the user's authToken if it exists or fetches a new one with a refresh token if it exists
     */
    async getAuthToken(): Promise<string> {
        const authToken = await this.tokenStore.getAuthToken();
        const refreshToken = await this.tokenStore.getRefreshToken();
        if (refreshToken === undefined) {
            // if there is no refresh token just pass through the auth token
            // particularly important for the external token case in the User class with authorizers
            return authToken;
        }
        const payload = jwtDecode<JwtPayload>(authToken);
        if (payload !== undefined && this._validJWTPayload(payload)) {
            return Promise.resolve(authToken);
        }
        return this.refresh().then((authResult) => authResult.auth_token);
    }

    /**
     * refresh gets and saves a new authToken for the currently authenticated user using their refresh token
     * @return {string} returns the user's authToken if successful
     */
    async refresh(): Promise<authResult> {
        const err = new PassageError('Login required', {
            status: PassageErrorCode.PSGLoginRequired,
            statusText: 'Login required',
        });
        const refreshToken = this.tokenStore.getRefreshToken();
        if (!refreshToken) return Promise.reject(err);
        return fetch(this.apiUrl, {
            headers: getHeaders(),
            method: 'POST',
            body: JSON.stringify({ refresh_token: refreshToken }),
        })
            .then(handleResponse)
            .then((response: { auth_result: authResult }) => {
                if (isFullTokenStore(this.tokenStore)) {
                    this.tokenStore.setTokens(response.auth_result);
                }
                return response.auth_result;
            })
            .catch(() => {
                throw err;
            });
    }

    /**
     * _validJWTPayload checks to see if the current JWT is valid (formatted as expected and isn't expired).
     * @param {JwtPayload} payload the payload from the JWT.
     * @return {boolean} true if the JWT payload is valid, false if not.
     */
    private _validJWTPayload(payload: JwtPayload): boolean {
        const expectedKeys = ['exp', 'iss', 'sub'];
        const currentTime = Math.floor(Date.now() / 1000);

        expectedKeys.forEach((expectedKey) => {
            if (!(expectedKey in payload)) return false;
        });

        if (payload.exp && currentTime > payload.exp) return false;
        return true;
    }

    /**
     * _validJWTHeader checkts to see if the JWT header is valid (formatted as expected).
     * @param {JwtHeader} header the header from the JWT.
     * @return {boolean} true if the JWT header is valid, false if not.
     */
    private _validJWTHeader(header: JwtHeader): boolean {
        const expectedObject = { alg: 'RS256', typ: 'JWT' };

        if (header.alg !== expectedObject.alg) return false;

        if (header.typ !== expectedObject.typ) return false;
        return true;
    }
}
import { env } from '../config/Environment';

/**
 * Builds the base URL for Passage backend operations
 * @param {string} appID The app ID
 * @return {string} base URL
 */
export function getBaseURL(appID: string): string {
    const baseUrl = env.URL;
    const apiVersion = 'v1';
    return `${baseUrl}/${apiVersion}/apps/${appID}`;
}
/* eslint-disable @typescript-eslint/no-explicit-any */
/**
 * Call an async function with a maximum time limit (in milliseconds) for the timeout
 * @param {Promise<any>} asyncPromise An asynchronous promise to resolve
 * @param {number} timeLimit Time limit to attempt function in milliseconds
 * @return {Promise<any>} Resolved promise for async function call
 */
export const asyncCallWithTimeout = async (
    asyncPromise: Promise<any>,
    timeLimit: number
): Promise<any> => {
    let timeoutHandle: any;

    const timeoutPromise = new Promise((resolve) => {
        timeoutHandle = setTimeout(resolve, timeLimit);
    });

    return Promise.race([asyncPromise, timeoutPromise]).then((result) => {
        clearTimeout(timeoutHandle);
        return result;
    });
};
