import { ErrorObject, serializeError } from 'serialize-error';
import { env } from './config/Environment';

export type PassageSentryProject = 'passage-js' | 'passage-elements';

export interface ISentryConfig {
    url?: string;
    project?: PassageSentryProject;
    dsn?: string;
    environment?: string;
    release?: string;
    version?: string;
}

export interface ICaptureEventProps {
    error?: Error | string;
    message?: string;
    sentryConfig?: ISentryConfig;
}

export interface CaptureEventBody {
    error?: Error | string;
    serializedError?: ErrorObject;
    message?: string;
    project: PassageSentryProject;
    release?: string;
    dsn?: string;
    environment?: string;
    version?: string;
}

export const cleanSentryConfig = (config: ISentryConfig): ISentryConfig => {
    return JSON.parse(
        JSON.stringify(config, (key, value): unknown => {
            if (value === null || value === undefined || value === 'undefined') {
                return undefined;
            }
            return value;
        })
    );
};

export const captureEvent = async (props: ICaptureEventProps): Promise<void> => {
    try {
        const {
            error,
            message,
            sentryConfig: {
                url = env.CAPTURE_EVENT_URL,
                project = env.SENTRY_PROJECT,
                dsn = env.SENTRY_PROJECT_DSN,
                environment = env.SENTRY_PROJECT_ENV,
                release = env.SENTRY_RELEASE,
                version = env.SENTRY_PROJECT_VERSION,
            } = {},
        } = props;

        const sentryConfig = cleanSentryConfig({
            project,
            dsn,
            environment,
            url,
            release,
            version,
        });

        if (!(sentryConfig.project && sentryConfig.url) || !(error || message)) {
            return;
        }

        let body: CaptureEventBody = {
            project: sentryConfig.project,
            dsn: sentryConfig.dsn,
            environment: sentryConfig.environment,
            release: sentryConfig.release,
            version: sentryConfig.version,
        };

        if (message) {
            body = {
                ...body,
                message,
            };
        }

        if (error) {
            body = {
                ...body,
                serializedError: serializeError(
                    typeof error === 'string' ? new Error(error) : error
                ),
            };
        }

        await fetch(sentryConfig.url, {
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
            },
            method: 'POST',
            body: JSON.stringify(body),
        });
    } catch (error) {
        console.error(error);
    }
    return;
};

export default captureEvent;
